"use strict";

module.exports = {
    hooks: {
        'finish': function() {
            if (this.output.name !== 'website') {
                return;
            }
            if (this.options.pluginsConfig && this.options.pluginsConfig["robots-txt"]) {
                var options = this.options.pluginsConfig["robots-txt"];
            }
            var filepath = options.path ? options.path : './robots.txt';

            var fs = require('fs');

            if (fs.existsSync(filepath)) {
                this.log.debug.ln('Copying %s to robots.txt', filepath);
                return this.output.copyFile(filepath, "robots.txt");
            }
            else {
                this.log.warn.ln('Unable to find robots.txt at %s. Are you sure it exists?', filepath);
            }
        }
    }
};